package gui.layout;

/**
 * Mapping for the panes in the pane pool generated by the
 * LayoutManager.
 *
 * @author Janos Benyovszki
 * @see LayoutManager
 */
public interface LayoutPoolMap {

    static final int FRAME = 0;
    static final int START = 1;
    static final int TRANSACTIONS = 2;
    static final int DATA = 3;

    //TODO enum class instead of Strings - multiple strings can be mixed now

    /*
    * Resource bundles can be in format text/[name], as the ResourceBundle
    * class looks for the in the resources folder. For fxml this does not
    * work as the LayoutManager uses getClass(), it only goes up to gui.
    *
    * Using classloader sets the relative path to target/classes/, then
    * getResources returns the fxml if the path is fxml/[name]
    * */
    static final String FRAME_LOCATION = "fxml/Frame.fxml";
    static final String FRAME_RESOURCE_LOCATION = "text/Frame_en";
    static final String TRANSACTION_LOCATION = "fxml/Transactions.fxml";
    static final String TRANSACTION_RESOURCE_LOCATION = "text/Transactions";
    static final String START_LOCATION = "fxml/Start.fxml";
    static final String START_RESOURCE_LOCATION = "text/Start_en";
    static final String DATA_LOCATION = "fxml/Data.fxml";
    static final String DATA_RESOURCE_LOCATION = "text/Data_en";

}
